    const range = document.createRange()

    // ===============================
    // length 大于 1
    // 代表着转换是一个文档节点多段落
    // ===============================
    if ($node.length > 1) {
        range.setStart($node.elems[0], 0)
        range.setEnd($node.elems[$node.length - 1], $node.elems[$node.length - 1].childNodes.length)
