                $li.on('click', (e: Event) => {
                    clickHandler(value)

                    // 阻止冒泡
                    e.stopPropagation()

                    // item 点击之后，隐藏 list
                    this.hideTimeoutId = window.setTimeout(() => {
                        this.hide()
                    })
                })
            }
        })
        $container.append($list)

        // 绑定隐藏事件
        $container.on('mouseleave', () => {
            this.hideTimeoutId = window.setTimeout(() => {
                this.hide()
            })
        })

        // 记录属性
        this.$container = $container
        this.rendered = false
        this._show = false
    }

    /**
     * 显示 DropList
     */
    public show() {
        if (this.hideTimeoutId) {
            // 清除之前的定时隐藏
            clearTimeout(this.hideTimeoutId)
        }

        const menu = this.menu
        const $menuELem = menu.$elem
        const $container = this.$container
        if (this._show) {
            return
        }
        if (this.rendered) {
            // 显示
            $container.show()
        } else {
            // 加入 DOM 之前先定位位置
            const menuHeight = $menuELem.getBoundingClientRect().height || 0
            const width = this.conf.width || 100 // 默认为 100
            $container.css('margin-top', menuHeight + 'px').css('width', width + 'px')

            // 加入到 DOM
            $menuELem.append($container)
            this.rendered = true
        }

        // 修改属性
        this._show = true
    }

    /**
     * 隐藏 DropList
     */
    public hide() {
        const $container = this.$container
        if (!this._show) {
            return
        }
        // 隐藏并需改属性
        $container.hide()
        this._show = false
    }

    public get isShow() {
        return this._show
    }
}

export default DropList
/**
 * @description 上传的核心方法
 * @author wangfupeng
 */

import { forEach } from '../../utils/util'
import { DicType } from '../../config/index'

type PostOptionType<T> = {
    timeout?: number
    formData?: FormData
    headers?: DicType
    withCredentials?: boolean
    onTimeout?: (xhr: XMLHttpRequest) => void
    onProgress?: (percent: number, event: ProgressEvent) => void
    beforeSend?: (xhr: XMLHttpRequest) => { prevent: boolean; msg: string } | void
    onError?: (xhr: XMLHttpRequest) => void
    onFail?: (xhr: XMLHttpRequest, msg: string) => void
    onSuccess: (xhr: XMLHttpRequest, result: T) => void
}

/**
 * 发送 post 请求（用于文件上传）
 * @param url url
 * @param option 配置项
 */
function post<T extends Object>(url: string, option: PostOptionType<T>): XMLHttpRequest | string {
    const xhr = new XMLHttpRequest()
    xhr.open('POST', url)

    // 超时，默认 10s
    xhr.timeout = option.timeout || 10 * 1000
    xhr.ontimeout = () => {
        console.error('wangEditor - 请求超时')
        option.onTimeout && option.onTimeout(xhr)
    }

    // 进度
    if (xhr.upload) {
        xhr.upload.onprogress = (e: ProgressEvent) => {
            const percent = e.loaded / e.total
            option.onProgress && option.onProgress(percent, e)
        }
    }

    // 自定义 header
    if (option.headers) {
        forEach(option.headers, (key: string, val: string) => {
            xhr.setRequestHeader(key, val)
        })
    }

    // 跨域传 cookie
    xhr.withCredentials = !!option.withCredentials

    // 上传之前的钩子函数，在 xhr.send() 之前执行
    if (option.beforeSend) {
        const beforeResult = option.beforeSend(xhr)
        if (beforeResult && typeof beforeResult === 'object') {
            if (beforeResult.prevent) {
                // 如果返回的结果是 {prevent: true, msg: 'xxxx'} 则表示用户放弃上传
                return beforeResult.msg
            }
        }
    }

    // 服务端返回之后
    xhr.onreadystatechange = () => {
        if (xhr.readyState !== 4) return
        const status = xhr.status
        if (status < 200) return // 请求发送过程中，尚未返回
        if (status >= 300 && status < 400) return // 重定向
        if (status >= 400) {
            // 40x 50x 报错
            console.error('wangEditor - XHR 报错，状态码 ' + status)
            if (option.onError) option.onError(xhr) // 有，则执行 onError 函数即可
            return
        }

        // status = 200 ，得到结果
        const resultStr = xhr.responseText
        let result: T
        if (typeof resultStr !== 'object') {
            try {
                result = JSON.parse(resultStr)
            } catch (ex) {
                console.error('wangEditor - 返回结果不是 JSON 格式', resultStr)
                if (option.onFail) option.onFail(xhr, resultStr)
                return
            }
        } else {
            result = resultStr
        }
        option.onSuccess(xhr, result)
    }

    // 发送请求
    xhr.send(option.formData || null)

    return xhr
}

export default post
/**
 * @description 上传进度条
 * @author wangfupeng
 */

import $, { DomElement } from '../../utils/dom-core'
import Editor from '../../editor/index'

class Progress {
    private editor: Editor
    private $bar: DomElement
    private $textContainer: DomElement
    private isShow: boolean
    private time: number
    private timeoutId: number

    constructor(editor: Editor) {
        this.editor = editor
        this.$textContainer = editor.$textContainerElem
        this.$bar = $('<div class="w-e-progress"></div>')

        this.isShow = false
        this.time = 0
        this.timeoutId = 0
    }

    /**
     * 显示进度条
     * @param progress 进度百分比
     */
    public show(progress: number): void {
        // 不要重新显示
        if (this.isShow) {
            return
        }
        this.isShow = true

        // 渲染 $bar
        const $bar = this.$bar
        const $textContainer = this.$textContainer
        $textContainer.append($bar)

        // 改变进度条（防抖，100ms 渲染一次）
        if (Date.now() - this.time > 100) {
            if (progress <= 1) {
                $bar.css('width', progress * 100 + '%')
                this.time = Date.now()
            }
        }

        // 500ms 之后隐藏
        let timeoutId = this.timeoutId
        if (timeoutId) {
            clearTimeout(timeoutId)
        }
        this.timeoutId = window.setTimeout(() => {
            this.hide()
        }, 500)
    }

    /**
     * 隐藏
     */
    private hide() {
        const $bar = this.$bar
        $bar.remove()

        this.isShow = false
        this.time = 0
        this.timeoutId = 0
    }
}

export default Progress
/**
 * @description 无序列表/有序列表
 * @author tonghan
 */

import $, { DomElement } from '../../utils/dom-core'
import Editor from '../../editor/index'
import DropListMenu from '../menu-constructors/DropListMenu'
import { MenuActive } from '../menu-constructors/Menu'

import { updateRange } from './utils'

import { HandlerListOptions } from './ListHandle/ListHandle'
import ListHandleCommand, { createListHandle, ClassType } from './ListHandle'

/**
 * 列表的种类
 */
export enum ListType {
    OrderedList = 'OL',
    UnorderedList = 'UL',
}

// 序列类型
type ListTypeValue = ListType

class List extends DropListMenu implements MenuActive {
    constructor(editor: Editor) {
        const $elem = $(
            `<div class="w-e-menu" data-title="序列">
                <i class="w-e-icon-list2"></i>
            </div>`
        )

        const dropListConf = {
            width: 130,
            title: '序列',
            type: 'list',
            list: [
                {
                    $elem: $(`
                        <p>
                            <i class="w-e-icon-list2 w-e-drop-list-item"></i>
                            ${editor.i18next.t('menus.dropListMenu.list.无序列表')}
                        <p>`),
                    value: ListType.UnorderedList,
                },

                {
                    $elem: $(
                        `<p>
                            <i class="w-e-icon-list-numbered w-e-drop-list-item"></i>
                            ${editor.i18next.t('menus.dropListMenu.list.有序列表')}
                        <p>`
                    ),
                    value: ListType.OrderedList,
                },
            ],
            clickHandler: (value: string) => {
                // 注意 this 是指向当前的 List 对象
                this.command(value as ListTypeValue)
            },
        }

        super($elem, editor, dropListConf)
    }

    public command(type: ListTypeValue): void {
        const editor = this.editor
        const $selectionElem = editor.selection.getSelectionContainerElem()

        // 选区范围的 DOM 元素不存在，不执行命令
        if ($selectionElem === undefined) return

        // 获取选区范围内的顶级 DOM 元素
        this.handleSelectionRangeNodes(type)

        // 是否激活
        this.tryChangeActive()
    }

    public validator($startElem: DomElement, $endElem: DomElement, $textElem: DomElement): boolean {
        if (
            !$startElem.length ||
            !$endElem.length ||
            $textElem.equal($startElem) ||
            $textElem.equal($endElem)
        ) {
            return false
        }

        return true
    }

    private handleSelectionRangeNodes(listType: ListTypeValue): void {
        const editor = this.editor
        const selection = editor.selection

        // 获取 序列标签
        const listTarget = listType.toLowerCase()

        // 获取相对应的 元属节点
        let $selectionElem = selection.getSelectionContainerElem() as DomElement
        const $startElem = (selection.getSelectionStartElem() as DomElement).getNodeTop(editor)
        const $endElem = (selection.getSelectionEndElem() as DomElement).getNodeTop(editor)

        // 验证是否执行 处理逻辑
        if (!this.validator($startElem, $endElem, editor.$textElem)) {
            return
        }

        // 获取选区
        const _range = selection.getRange()
        const _collapsed = _range?.collapsed

        // 防止光标的时候判断异常
        if (!editor.$textElem.equal($selectionElem)) {
            $selectionElem = $selectionElem.getNodeTop(editor)
        }

        const options: HandlerListOptions = {
            editor,
            listType,
            listTarget,
            $selectionElem,
            $startElem,
            $endElem,
        }

        let classType: ClassType

        // =====================================
        // 当 selectionElem 属于序列元素的时候
        // 代表着当前选区一定是在一个序列元素内的
        // =====================================
        if (this.isOrderElem($selectionElem as DomElement)) {
            classType = ClassType.Wrap
        }

        // =====================================
        // 当 startElem 和 endElem 属于序列元素的时候
        // 代表着当前选区一定是在再两个序列的中间(包括两个序列)
        // =====================================
        else if (
            this.isOrderElem($startElem as DomElement) &&
            this.isOrderElem($endElem as DomElement)
        ) {
            classType = ClassType.Join
        }

        // =====================================
        // 选区开始元素为 序列 的时候
        // =====================================
        else if (this.isOrderElem($startElem as DomElement)) {
            classType = ClassType.StartJoin
        }

        // =====================================
        // 选区结束元素为 序列 的时候
        // =====================================
        else if (this.isOrderElem($endElem as DomElement)) {
            classType = ClassType.EndJoin
        }

        // =====================================
        // 当选区不是序列内且开头和结尾不是序列的时候
        // 直接获取所有顶级段落然后过滤
        // 代表着 设置序列 的操作
        // =====================================
        else {
            classType = ClassType.Other
        }

        const listHandleCmd = new ListHandleCommand(
            createListHandle(classType, options, _range as Range)
        )

        // 更新选区
        updateRange(editor, listHandleCmd.getSelectionRangeElem(), !!_collapsed)
    }

    /**
     * 是否是序列元素节点 UL and OL
     * @param $node
     */
    private isOrderElem($node: DomElement) {
        const nodeName = $node.getNodeName()
        if (nodeName === ListType.OrderedList || nodeName === ListType.UnorderedList) {
            return true
        }
        return false
    }

    public tryChangeActive(): void {}
}

export default List
