import Editor from '../../../editor/index'
import $ from '../../../utils/dom-core'
import { getCursorNextNode, isAllTodo } from '../util'
import createTodo from '../todo'
import { dealTextNode, isTodo } from '../util'
import { EMPTY_P } from '../../../utils/const'

/**
 * todolist 内部逻辑
 * @param editor
 */
function bindEvent(editor: Editor) {
    /**
     * todo的自定义回车事件
     * @param e 事件属性
     */
    function todoEnter(e: Event) {
        // 判断是否为todo节点
        if (isAllTodo(editor)) {
            e.preventDefault()
            const selection = editor.selection
            const $topSelectElem = selection.getSelectionRangeTopNodes()[0]
            const $li = $topSelectElem.childNodes()?.get(0)
            const selectionNode = window.getSelection()?.anchorNode as Node
            const range = selection.getRange()

            if (!range?.collapsed) {
                const rangeChildNodes = range?.commonAncestorContainer.childNodes
                const startContainer = range?.startContainer as Node
                const endContainer = range?.endContainer as Node
                const startPos = range?.startOffset as number
                const endPos = range?.endOffset as number
                let startElemIndex: number = 0
                let endElemIndex: number = 0
                let delList: ChildNode[] = []
                // 找出startContainer和endContainer在rangeChildNodes中的位置
                rangeChildNodes?.forEach((v, i) => {
                    if (v.contains(startContainer)) startElemIndex = i
                    if (v.contains(endContainer)) endElemIndex = i
                })
                // 删除两个容器间的内容
                if (endElemIndex - startElemIndex > 1) {
                    rangeChildNodes?.forEach((v, i) => {
                        if (i <= startElemIndex) return
                        if (i >= endElemIndex) return
                        delList.push(v)
                    })
                    delList.forEach(v => {
                        v.remove()
                    })
                }
                // 删除两个容器里拖蓝的内容
                dealTextNode(startContainer, startPos)
                dealTextNode(endContainer, endPos, false)
                editor.selection.moveCursor(endContainer, 0)
            }

            // 回车时内容为空时，删去此行
            if ($topSelectElem.text() === '') {
                const $p = $(EMPTY_P)
                $p.insertAfter($topSelectElem)
                selection.moveCursor($p.getNode())
                $topSelectElem.remove()
                return
            }

            const pos = selection.getCursorPos() as number
            const CursorNextNode = getCursorNextNode($li?.getNode() as Node, selectionNode, pos)
            const todo = createTodo($(CursorNextNode))
            const $inputcontainer = todo.getInputContainer()
            const todoLiElem = $inputcontainer.parent().getNode()
            const $newTodo = todo.getTodo()
            const contentSection = $inputcontainer.getNode().nextSibling
            // 处理光标在最前面时回车input不显示的问题
            if ($li?.text() === '') {
                $li?.append($(`<br>`))
            }
            $newTodo.insertAfter($topSelectElem)
            // 处理在google中光标在最后面的，input不显示的问题(必须插入之后移动光标)
            if (!contentSection || contentSection?.textContent === '') {
                // 防止多个br出现的情况
                if (contentSection?.nodeName !== 'BR') {
                    const $br = $(`<br>`)
                    $br.insertAfter($inputcontainer)
                }
                selection.moveCursor(todoLiElem, 1)
            } else {
                selection.moveCursor(todoLiElem)
            }
        }
    }

    /**
     * 自定义删除事件，用来处理光标在最前面删除input产生的问题
     */
    function delDown(e: Event) {
        if (isAllTodo(editor)) {
            const selection = editor.selection
            const $topSelectElem = selection.getSelectionRangeTopNodes()[0]
            const $li = $topSelectElem.childNodes()?.getNode()
            const $p = $(`<p></p>`)
            const p = $p.getNode()
            const selectionNode = window.getSelection()?.anchorNode as Node
            const pos = selection.getCursorPos()
            const prevNode = selectionNode.previousSibling

            // 处理内容为空的情况
            if ($topSelectElem.text() === '') {
                e.preventDefault()
                const $newP = $(EMPTY_P)
                $newP.insertAfter($topSelectElem)
                $topSelectElem.remove()
                selection.moveCursor($newP.getNode(), 0)
                return
            }

            // 处理有内容时，光标在最前面的情况
            if (
                prevNode?.nodeName === 'SPAN' &&
                prevNode.childNodes[0].nodeName === 'INPUT' &&
                pos === 0
            ) {
                e.preventDefault()
                $li?.childNodes.forEach((v, index) => {
                    if (index === 0) return
                    p.appendChild(v.cloneNode(true))
                })
                $p.insertAfter($topSelectElem)

                $topSelectElem.remove()
            }
        }
    }

    /**
     * 自定义删除键up事件
     */
    function deleteUp() {
        const selection = editor.selection
        const $topSelectElem = selection.getSelectionRangeTopNodes()[0]
        if ($topSelectElem && isTodo($topSelectElem)) {
            if ($topSelectElem.text() === '') {
                $(EMPTY_P).insertAfter($topSelectElem)
                $topSelectElem.remove()
            }
        }
    }

    /**
     * input 的点击事件（ input 默认不会产生 attribute 的改变 ）
     * @param e 事件属性
     */
    function inputClick(e?: Event): void {
        if (e && e.target instanceof HTMLInputElement) {
            if (e.target.type === 'checkbox') {
                if (e.target.checked) {
                    e.target.setAttribute('checked', 'true')
                } else {
                    e.target.removeAttribute('checked')
                }
            }
        }
    }

    editor.txt.eventHooks.enterDownEvents.push(todoEnter)
    editor.txt.eventHooks.deleteUpEvents.push(deleteUp)
    editor.txt.eventHooks.deleteDownEvents.push(delDown)
    editor.txt.eventHooks.clickEvents.push(inputClick)
}

export default bindEvent
/**
 * @description 初始化编辑器 DOM 结构
 * @author wangfupeng
 */

import Editor from '../index'
import $, { DomElement } from '../../utils/dom-core'
import { getRandom } from '../../utils/util'
import { EMPTY_P } from '../../utils/const'
import CONFIG from '../../config/text'
const styleSettings = {
    border: '1px solid #c9d8db',
    toolbarBgColor: '#FFF',
    toolbarBottomBorder: '1px solid #EEE',
}

export default function (editor: Editor): void {
    const toolbarSelector = editor.toolbarSelector
    const $toolbarSelector = $(toolbarSelector)
    const textSelector = editor.textSelector

    const config = editor.config
    const height = config.height
    const i18next = editor.i18next

    const $toolbarElem: DomElement = $('<div></div>')
    const $textContainerElem: DomElement = $('<div></div>')
    let $textElem: DomElement
    let $children: DomElement | null
    let $subChildren: DomElement | null = null

    if (textSelector == null) {
        // 将编辑器区域原有的内容，暂存起来
        $children = $toolbarSelector.children()

        // 添加到 DOM 结构中
        $toolbarSelector.append($toolbarElem).append($textContainerElem)

        // 自行创建的，需要配置默认的样式
        $toolbarElem
            .css('background-color', styleSettings.toolbarBgColor)
            .css('border', styleSettings.border)
            .css('border-bottom', styleSettings.toolbarBottomBorder)
        $textContainerElem
            .css('border', styleSettings.border)
            .css('border-top', 'none')
            .css('height', `${height}px`)
    } else {
        // toolbarSelector 和 textSelector 都有
        $toolbarSelector.append($toolbarElem)
        // 菜单分离后，文本区域内容暂存
        $subChildren = $(textSelector).children()
        $(textSelector).append($textContainerElem)
        // 将编辑器区域原有的内容，暂存起来
        $children = $textContainerElem.children()
    }

    // 编辑区域
    $textElem = $('<div></div>')
    $textElem.attr('contenteditable', 'true').css('width', '100%').css('height', '100%')

    // 添加 placeholder
    let $placeholder: DomElement
    const placeholder = editor.config.placeholder
    if (placeholder !== CONFIG.placeholder) {
        $placeholder = $(`<div>${placeholder}</div>`)
    } else {
        $placeholder = $(`<div>${i18next.t(placeholder)}</div>`)
    }
    $placeholder.addClass('placeholder')

    // 初始化编辑区域内容
    if ($children && $children.length) {
        $textElem.append($children)
        // 编辑器有默认值的时候隐藏placeholder
        $placeholder.hide()
    } else {
        $textElem.append($(EMPTY_P)) // 新增一行，方便继续编辑
    }

    // 菜单分离后，文本区域有标签的带入编辑器内
    if ($subChildren && $subChildren.length) {
        $textElem.append($subChildren)
        // 编辑器有默认值的时候隐藏placeholder
        $placeholder.hide()
    }

    // 编辑区域加入DOM
    $textContainerElem.append($textElem)

    // 添加placeholder
    $textContainerElem.append($placeholder)

    // 设置通用的 class
    $toolbarElem.addClass('w-e-toolbar').css('z-index', editor.zIndex.get('toolbar'))
    $textContainerElem.addClass('w-e-text-container')
    $textContainerElem.css('z-index', editor.zIndex.get())
    $textElem.addClass('w-e-text')

    // 添加 ID
    const toolbarElemId = getRandom('toolbar-elem')
    $toolbarElem.attr('id', toolbarElemId)
    const textElemId = getRandom('text-elem')
    $textElem.attr('id', textElemId)

    // 判断编辑区与容器高度是否一致
    const textContainerCliheight = $textContainerElem.getBoundingClientRect().height
    const textElemClientHeight = $textElem.getBoundingClientRect().height
    if (textContainerCliheight !== textElemClientHeight) {
        $textElem.css('min-height', textContainerCliheight + 'px')
    }

    // 记录属性
    editor.$toolbarElem = $toolbarElem
    editor.$textContainerElem = $textContainerElem
    editor.$textElem = $textElem
    editor.toolbarElemId = toolbarElemId
    editor.textElemId = textElemId
}

/**
 * 工具栏/文本区域 DOM selector 有效性验证
 * @param editor 编辑器实例
 */
export function selectorValidator(editor: Editor) {
    const name = 'data-we-id'
    const regexp = /^wangEditor-\d+$/
    const { textSelector, toolbarSelector } = editor

    const $el = {
        bar: $('<div></div>'),
        text: $('<div></div>'),
    }

    if (toolbarSelector == null) {
        throw new Error('错误：初始化编辑器时候未传入任何参数，请查阅文档')
    } else {
        $el.bar = $(toolbarSelector)
        if (!$el.bar.elems.length) {
            throw new Error(`无效的节点选择器：${toolbarSelector}`)
        }
        if (regexp.test($el.bar.attr(name))) {
            throw new Error('初始化节点已存在编辑器实例，无法重复创建编辑器')
        }
    }
    if (textSelector) {
        $el.text = $(textSelector)
        if (!$el.text.elems.length) {
            throw new Error(`无效的节点选择器：${textSelector}`)
        }
        if (regexp.test($el.text.attr(name))) {
            throw new Error('初始化节点已存在编辑器实例，无法重复创建编辑器')
        }
    }

    // 给节点做上标记
    $el.bar.attr(name, editor.id)
    $el.text.attr(name, editor.id)

    // 在编辑器销毁前取消标记
    editor.beforeDestroy(function () {
        $el.bar.removeAttr(name)
        $el.text.removeAttr(name)
    })
}
