            return this.baseZIndex + this.tier[tierName]
        }
        return this.baseZIndex
    }

    // 初始化
    init(editor: Editor) {
        if (this.baseZIndex == style.zIndex) {
            this.baseZIndex = editor.config.zIndex
        }
    }
}
