import { EMPTY_FN } from '../../utils/const'

/**
 * 剔除编辑区容器的 attribute 变化中的非 contenteditable 变化
 * @param mutations MutationRecord[]
 * @param tar 编辑区容器的 DOM 节点
 */
function mutationsFilter(mutations: MutationRecord[], tar: Node) {
    // 剔除编辑区容器的 attribute 变化中的非 contenteditable 变化
    return mutations.filter(({ type, target, attributeName }) => {
        return (
            type != 'attributes' ||
            (type == 'attributes' && (attributeName == 'contenteditable' || target != tar))
        )
    })
}

/**
 * Change 实现
 */
export default class Change extends Mutation {
    /**
     * 变化的数据集合
     */
    private data: MutationRecord[] = []

    /**
     * 异步保存数据
     */
    private asyncSave: Function = EMPTY_FN

    constructor(public editor: Editor) {
        super((mutations, observer) => {
            // 数据过滤
            mutations = mutationsFilter(mutations, observer.target as Node)

            // 存储数据
            this.data.push(...mutations)

            // 标准模式下
            if (!editor.isCompatibleMode) {
                // 在非中文输入状态下时才保存数据
                if (!editor.isComposing) {
                    return this.asyncSave()
                }
            }
            // 兼容模式下
            else {
                this.asyncSave()
            }
        })
    }

    /**
     * 保存变化的数据并发布 change event
     */
    private save() {
        // 有数据
        if (this.data.length) {
            // 保存变化数据
            this.editor.history.save(this.data)

            // 清除缓存
            this.data.length = 0

            this.emit()
        }
    }

    /**
     * 发布 change event
     */
    public emit() {
        // 执行 onchange 回调
        this.editor.txt.eventHooks.changeEvents.forEach(fn => fn())
    }

    // 重写 observe
    public observe() {
        super.observe(this.editor.$textElem.elems[0])

        let timeout = this.editor.config.onchangeTimeout
        this.asyncSave = debounce(() => {
            this.save()
        }, timeout)

        if (!this.editor.isCompatibleMode) {
            this.editor.$textElem.on('compositionend', () => {
                this.asyncSave()
            })
        }
    }
}
/**
 * @description 封装 MutationObserver
 * @author fangzhicong
 */

export type callback = (mutations: MutationRecord[], observer: Mutation) => void

/**
 * 封装 MutationObserver，抽离成公共类
 */
export default class Mutation {
    /**
     * MutationObserver 实例
     */
    protected observer: MutationObserver

    /**
     * 被监听的 Node 节点（可继承的，方便扩展但又不会在外部被修改）
     */
    protected node?: Node

    /**
     * 默认的 MutationObserverInit 配置
     */
    protected options: MutationObserverInit = {
        subtree: true,
        childList: true,
        attributes: true,
        attributeOldValue: true,
        characterData: true,
        characterDataOldValue: true,
    }

    /**
     * MutationCallback
     */
    protected callback: (mutations: MutationRecord[]) => void

    /**
     * 构造器
     * @param fn 发生变化时执行的回调函数
     * @param options 自定义配置项
     */
    constructor(fn: callback, options?: MutationObserverInit) {
        this.callback = mutations => {
            fn(mutations, this)
        }
        this.observer = new MutationObserver(this.callback)
        options && (this.options = options)
    }

    public get target() {
        return this.node
    }

    /**
     * 绑定监听节点（初次绑定有效）
     * @param node 需要被监听的节点
     */
    public observe(node: Node) {
        if (!(this.node instanceof Node)) {
            this.node = node
            this.connect()
        }
    }

    /**
     * 连接监听器（开始观察）
     */
    public connect() {
        if (this.node) {
            this.observer.observe(this.node, this.options)
            return this
        }
        throw new Error('还未初始化绑定，请您先绑定有效的 Node 节点')
    }

    /**
     * 断开监听器（停止观察）
     */
    public disconnect() {
        let list = this.observer.takeRecords()
        list.length && this.callback(list)
        this.observer.disconnect()
    }
}
/**
 * @description 历史记录
 * @author fangzhicong
 */

import ContentCache from './data/content'
import ScrollCache from './data/scroll'
import RangeCache from './data/range'
import Editor from '../index'

/**
 * 历史记录（撤销、恢复）
 */
export default class History {
    // 数据缓存器实例
    private content: ContentCache

    // scroll 缓存
    private scroll: ScrollCache

    // Range 缓存
    private range: RangeCache

    constructor(public editor: Editor) {
        this.content = new ContentCache(editor)
        this.scroll = new ScrollCache(editor)
        this.range = new RangeCache(editor)
    }

    /**
     *  获取缓存中的数据长度。格式为：[正常的数据的条数，被撤销的数据的条数]
     */
    public get size() {
        return this.scroll.size
    }

    /**
     * 初始化绑定。在 editor.create() 结尾时调用
     */
    public observe() {
        this.content.observe()
        this.scroll.observe()
        // 标准模式下才进行初始化绑定
        !this.editor.isCompatibleMode && this.range.observe()
    }

    /**
     * 保存数据
     */
    public save(mutations: MutationRecord[]) {
        if (mutations.length) {
            this.content.save(mutations)
            this.scroll.save()
            // 标准模式下才进行缓存
            !this.editor.isCompatibleMode && this.range.save()
        }
    }

    /**
     * 撤销
     */
    public revoke() {
        this.editor.change.disconnect()
        const res = this.content.revoke()
        if (res) {
            this.scroll.revoke()
            // 标准模式下才执行
            if (!this.editor.isCompatibleMode) {
                this.range.revoke()
                this.editor.$textElem.focus()
            }
        }
        this.editor.change.connect()
        // 如果用户在 onchange 中修改了内容（DOM），那么缓存中的节点数据可能不连贯了，不连贯的数据必将导致恢复失败，所以必须将用户的 onchange 处于监控状态中
        res && this.editor.change.emit()
    }

    /**
     * 恢复
     */
    public restore() {
        this.editor.change.disconnect()
        const res = this.content.restore()
        if (res) {
            this.scroll.restore()
            // 标准模式下才执行
            if (!this.editor.isCompatibleMode) {
                this.range.restore()
                this.editor.$textElem.focus()
            }
        }
        this.editor.change.connect()
        // 与 revoke 同理
        res && this.editor.change.emit()
    }
}
/**
 * @description 整合差异备份和内容备份，进行统一管理
 * @author fangzhicong
 */

import NodeCache from './node'
import HtmlCache from './html'
import Editor from '../../index'

export default class ContentCache {
    /**
     * 内容备份的管理器
     */
    public cache?: HtmlCache | NodeCache

    constructor(public editor: Editor) {}

    /**
     * 初始化绑定
     */
    observe() {
        if (this.editor.isCompatibleMode) {
            // 兼容模式（内容备份）
            this.cache = new HtmlCache(this.editor)
        } else {
            // 标准模式（差异备份/节点备份）
            this.cache = new NodeCache(this.editor)
        }
        this.cache.observe()
    }

    /**
     * 保存
     */
    public save(mutations: MutationRecord[]) {
        if (this.editor.isCompatibleMode) {
            ;(this.cache as HtmlCache).save()
        } else {
            ;(this.cache as NodeCache).compile(mutations)
        }
    }

    /**
     * 撤销
     */
    public revoke() {
        return this.cache?.revoke()
    }

    /**
     * 恢复
     */
    public restore() {
        return this.cache?.restore()
    }
}
/**
 * @description 差异备份
 * @author fangzhicong
 */

import Cache from '../../../../utils/data-structure/cache'
import compile from './compile'
import { revoke, restore } from './decompilation'
import { Compile } from '../type'
import Editor from '../../../index'

export default class NodeCache extends Cache<Compile[]> {
    constructor(public editor: Editor) {
        super(editor.config.historyMaxSize)
    }

    public observe() {
        this.resetMaxSize(this.editor.config.historyMaxSize)
    }

    /**
     * 编译并保存数据
     */
    public compile(data: MutationRecord[]) {
        this.save(compile(data))
        return this
    }

    /**
     * 撤销
     */
    public revoke() {
        return super.revoke(data => {
            revoke(data)
        })
    }

    /**
     * 恢复
     */
    public restore() {
        return super.restore(data => {
            restore(data)
        })
    }
}
/**
 * @description 数据结构 - 栈
 * @author fangzhicong
 */

/**
 * 栈（限制最大数据条数，栈满后可以继续入栈，而先入栈的数据将失效）
 */
// 取名灵感来自 Math.ceil，向上取有效值
export class CeilStack<T> {
    /**
     * 数据缓存
     */
    protected data: T[] = []

    /**
     * 栈的最大长度。为零则长度不限
     */
    protected max: number = 0

    /**
     * 标识是否重设过 max 值
     */
    protected reset: boolean = false

    constructor(max: number = 0) {
        max = Math.abs(max)
        max && (this.max = max)
    }

    /**
     * 允许用户重设一次 max 值
     */
    public resetMax(maxSize: number) {
        maxSize = Math.abs(maxSize)
        if (!this.reset && !isNaN(maxSize)) {
            this.max = maxSize
            this.reset = true
        }
    }

    /**
     * 当前栈中的数据条数
     */
    public get size() {
        return this.data.length
    }

    /**
     * 入栈
     * @param data 入栈的数据
     */
    public instack(data: T) {
        this.data.unshift(data)
        if (this.max && this.size > this.max) {
            this.data.length = this.max
        }
        return this
    }

    /**
     * 出栈
     */
    public outstack(): T | undefined {
        return this.data.shift()
    }

    /**
     * 清空栈
     */
    public clear() {
        this.data.length = 0
        return this
    }
}
/**
 * @description 数据整理
 * @author fangzhicong
 */

import { TargetPosition, DiffNodes, Compile } from '../type'
import { UA, toArray } from '../../../../utils/util'

/**
 * 数据类型
 */
export function compileType(data: string) {
    switch (data) {
        case 'childList':
            return 'node'
        case 'attributes':
            return 'attr'
        default:
            return 'text'
    }
}

/**
 * 获取当前的文本内容
 */
export function compileValue(data: MutationRecord) {
    switch (data.type) {
        case 'attributes':
            return (data.target as Element).getAttribute(data.attributeName as string) || ''
        case 'characterData':
            return data.target.textContent
        default:
            return ''
    }
}

/**
 * addedNodes/removedNodes
 */
export function complieNodes(data: MutationRecord) {
    const temp: DiffNodes = {}
    if (data.addedNodes.length) {
        temp.add = toArray(data.addedNodes)
    }
    if (data.removedNodes.length) {
        temp.remove = toArray(data.removedNodes)
    }
    return temp
}

/**
 * addedNodes/removedNodes 的相对位置
 */
export function compliePosition(data: MutationRecord) {
    let temp: TargetPosition
    if (data.previousSibling) {
        temp = {
            type: 'before',
            target: data.previousSibling,
        }
    } else if (data.nextSibling) {
        temp = {
            type: 'after',
            target: data.nextSibling,
        }
    } else {
        temp = {
            type: 'parent',
            target: data.target,
        }
    }
    return temp
}

/**
 * 补全 Firefox 数据的特殊标签
 */
const tag = ['UL', 'OL', 'H1', 'H2', 'H3', 'H4', 'H5', 'H6']

/**
 * 将 MutationRecord 转换成自定义格式的数据
 */
export default function compile(data: MutationRecord[]) {
    const temp: Compile[] = []
    // 以下两个变量是兼容 Firefox 时使用到的
    // 前一次操作为删除元素节点
    let removeNode: Node | false = false
    // 连续的节点删除记录
    const removeCache: Node[] = []
    data.forEach((record, index) => {
        const item: Compile = {
            type: compileType(record.type),
            target: record.target,
            attr: record.attributeName || '',
            value: compileValue(record) || '',
            oldValue: record.oldValue || '',
            nodes: complieNodes(record),
            position: compliePosition(record),
        }
        temp.push(item)
        // 兼容 Firefox，补全数据（这几十行代码写得吐血，跟 IE 有得一拼）
        if (!UA.isFirefox) {
            return
        }
        // 正常的数据：缩进、行高、超链接、对齐方式、引用、插入表情、插入图片、分割线、表格、插入代码
        // 普通的数据补全：标题（纯文本内容）、加粗、斜体、删除线、下划线、颜色、背景色、字体、字号、列表（纯文本内容）
        // 特殊的数据补全：标题（包含 HTMLElement）、列表（包含 HTMLElement 或 ul -> ol 或 ol -> ul 或 Enter）
        if (removeNode && record.addedNodes.length && record.addedNodes[0].nodeType == 1) {
            // 需要被全数据的目标节点
            const replenishNode = record.addedNodes[0]
            const replenishData: Compile = {
                type: 'node',
                target: replenishNode,
                attr: '',
                value: '',
                oldValue: '',
                nodes: {
                    add: [removeNode],
                },
                position: {
                    type: 'parent',
                    target: replenishNode,
                },
            }
            // 特殊的标签：['UL', 'OL', 'H1', 'H2', 'H3', 'H4', 'H5', 'H6']
            if (tag.indexOf(replenishNode.nodeName) != -1) {
                replenishData.nodes.add = toArray(replenishNode.childNodes)
                temp.push(replenishData)
            }
            // 上一个删除元素是文本节点
            else if (removeNode.nodeType == 3) {
                if (contains(replenishNode, removeCache)) {
                    replenishData.nodes.add = toArray(replenishNode.childNodes)
                }
                temp.push(replenishData)
            }
            // 上一个删除元素是 Element && 由近到远的删除元素至少有一个是需要补全数据节点的子节点
            else if (
                tag.indexOf(record.target.nodeName) == -1 &&
                contains(replenishNode, removeCache)
            ) {
                replenishData.nodes.add = toArray(replenishNode.childNodes)
                temp.push(replenishData)
            }
        }
        // 记录本次的节点信息
        if (item.type == 'node' && record.removedNodes.length == 1) {
            removeNode = record.removedNodes[0]
            removeCache.push(removeNode)
        } else {
            removeNode = false
            removeCache.length = 0
        }
    })
    return temp
}

// 删除元素的历史记录中包含有多少个目标节点的子元素
function contains(tar: Node, childs: Node[]) {
    let count = 0
    for (let i = childs.length - 1; i > 0; i--) {
        if (tar.contains(childs[i])) {
            count++
        } else {
            break
        }
    }
    return count
}
/**
 * @description 撤销 - 逆向操作，删除节点
 * @author fangzhicong
 */
import { Compile } from '../type'

/**
 * 将节点添加到 DOM 树中
 * @param data 数据项
 * @param list 节点集合（addedNodes 或 removedNodes）
 */
function insertNode(data: Compile, list: Node[]) {
    let reference = data.position.target
    switch (data.position.type) {
        // reference 在这些节点的前面
        case 'before':
            if (reference.nextSibling) {
                reference = reference.nextSibling
                list.forEach((item: Node) => {
                    data.target.insertBefore(item, reference)
                })
            } else {
                list.forEach((item: Node) => {
                    data.target.appendChild(item)
                })
            }
            break
        // reference 在这些节点的后面
        case 'after':
            list.forEach((item: Node) => {
                data.target.insertBefore(item, reference)
            })
            break
        // parent
        // reference 是这些节点的父节点
        default:
            list.forEach((item: Node) => {
                reference.appendChild(item)
            })
            break
    }
}

/* ------------------------------------------------------------------ 撤销逻辑 ------------------------------------------------------------------ */

function revokeNode(data: Compile) {
    for (let [relative, list] of Object.entries(data.nodes)) {
        switch (relative) {
            // 反向操作，将这些节点从 DOM 中移除
            case 'add':
                list.forEach((item: Node) => {
                    data.target.removeChild(item)
                })
                break
            // remove（反向操作，将这些节点添加到 DOM 中）
            default: {
                insertNode(data, list)
                break
            }
        }
    }
}

/**
 * 撤销 attribute
 */
function revokeAttr(data: Compile) {
    let target = data.target as HTMLElement
    if (data.oldValue == null) {
        target.removeAttribute(data.attr)
    } else {
        target.setAttribute(data.attr, data.oldValue)
    }
}

/**
 * 撤销文本内容
 */
function revokeText(data: Compile) {
    data.target.textContent = data.oldValue
}

const revokeFns = {
    node: revokeNode,
    text: revokeText,
    attr: revokeAttr,
}

// 撤销 - 对外暴露的接口
export function revoke(data: Compile[]) {
    for (let i = data.length - 1; i > -1; i--) {
        let item = data[i]
        revokeFns[item.type](item)
    }
}

/* ------------------------------------------------------------------ 恢复逻辑 ------------------------------------------------------------------ */

function restoreNode(data: Compile) {
    for (let [relative, list] of Object.entries(data.nodes)) {
        switch (relative) {
            case 'add': {
                insertNode(data, list)
                break
            }
            // remove
            default: {
                list.forEach((item: Node) => {
                    ;(item.parentNode as Node).removeChild(item)
                })
                break
            }
        }
    }
}

function restoreText(data: Compile) {
    data.target.textContent = data.value
}

function restoreAttr(data: Compile) {
    ;(data.target as HTMLElement).setAttribute(data.attr, data.value)
}

const restoreFns = {
    node: restoreNode,
    text: restoreText,
    attr: restoreAttr,
}

// 恢复 - 对外暴露的接口
export function restore(data: Compile[]) {
    for (let item of data) {
        restoreFns[item.type](item)
    }
}
/**
 * @description 完整的内容备份，每次变化都将备份整个编辑器的内容
 * @author fangzhicong
 */
import Editor from '../../../index'
import { TailChain } from '../../../../utils/data-structure/chain'

export default class HtmlCache {
    // 缓存数据
    private data: TailChain<string>

    constructor(public editor: Editor) {
        this.data = new TailChain()
    }

    /**
     * 初始化绑定
     */
    observe() {
        this.data.resetMax(this.editor.config.historyMaxSize)
        // 保存初始化值
        this.data.insertLast(this.editor.$textElem.html())
    }

    /**
     * 保存
     */
    public save() {
        this.data.insertLast(this.editor.$textElem.html())
        return this
    }

    /**
     * 撤销
     */
    public revoke() {
        let data = this.data.prev()
        if (data) {
            this.editor.$textElem.html(data)
            return true
        }
        return false
    }

    /**
     * 恢复
     */
    public restore() {
        let data = this.data.next()
        if (data) {
            this.editor.$textElem.html(data)
            return true
        }
        return false
    }
}
/**
 * @description 数据结构 - 链表
 * @author fangzhicong
 */

/**
 * 特殊链表（数据尾插入、插入前自动清理指针后边的数据、插入后指针永远定位于最后一位元素、可限制链表长度、指针双向移动）
 */
export class TailChain<T> {
    /**
     * 链表数据
     */
    protected data: T[] = []

    /**
     * 链表最大长度，零表示长度不限
     */
    protected max: number = 0

    /**
     * 指针
     */
    protected point = 0

    // 当前指针是否人为操作过
    protected isRe = false

    /**
     * 允许用户重设一次 max 值
     */
    resetMax(maxSize: number) {
        maxSize = Math.abs(maxSize)
        maxSize && (this.max = maxSize)
    }

    /**
     * 当前链表的长度
     */
    get size() {
        return this.data.length
    }

    /**
     * 尾插入
     * @param data 插入的数据
     */
    public insertLast<K extends T>(data: K) {
        // 人为操作过指针，清除指针后面的元素
        if (this.isRe) {
            this.data.splice(this.point + 1)
            this.isRe = false
        }
        this.data.push(data)
        // 超出链表最大长度
        while (this.max && this.size > this.max) {
            this.data.shift()
        }
        // 从新定位指针到最后一个元素
        this.point = this.size - 1
        return this
    }

    /**
     * 获取当前指针元素
     */
    public current(): T | undefined {
        return this.data[this.point]
    }

    /**
     * 获取上一指针元素
     */
    public prev(): T | undefined {
        !this.isRe && (this.isRe = true)
        this.point--
        if (this.point < 0) {
            this.point = 0
            return undefined
        }
        return this.current()
    }

    /**
     * 下一指针元素
     */
    public next(): T | undefined {
        !this.isRe && (this.isRe = true)
        this.point++
        if (this.point >= this.size) {
            this.point = this.size - 1
            return undefined
        }
        return this.current()
    }
}
/**
 * @description 记录 scrollTop
 * @author fangzhicong
 */

import Cache from '../../../../utils/data-structure/cache'
import Editor from '../../../index'
import { ScrollItem } from '../type'

export default class ScrollCache extends Cache<ScrollItem> {
    /**
     * 上一次的 scrollTop
     */
    private last: number = 0

    /**
     * 编辑区容器元素节点
     */
    private target: Element

    constructor(public editor: Editor) {
        super(editor.config.historyMaxSize)
        this.target = editor.$textElem.elems[0]
    }

